{
    "httpRequest": {
        "method": "OPTIONS"
    }, 
    "httpResponse": {
        "headers": [
            {
                "name": "Access-Control-Allow-Origin", 
                "values": [
                    "*"
                ]
            }, 
            {
                "name": "Access-Control-Allow-Methods", 
                "values": [
                    "DELETE, GET, HEAD, OPTIONS, POST, PUT, PATCH"
                ]
            }, 
            {
                "name": "Access-Control-Allow-Headers", 
                "values": [
                    "Allow, Content-Encoding, Content-Length, Content-Type, ETag, Expires, Last-Modified, Location, Server, Vary, Authorization"
                ]
            }, 
            {
                "name": "Access-Control-Expose-Headers", 
                "values": [
                    "Allow, Content-Encoding, Content-Length, Content-Type, ETag, Expires, Last-Modified, Location, Server, Vary, Authorization"
                ]
            }, 
            {
                "name": "Access-Control-Max-Age", 
                "values": [
                    "300"
                ]
            }, 
            {
                "name": "connection", 
                "values": [
                    "close"
                ]
            }
        ]
    }
}

{
    "httpRequest": {
        "path": "/templatePath"
    }, 
    "httpResponseTemplate": {
        "templateType": "JAVASCRIPT", 
        "template": "if (request.method === 'POST' && request.path === '/templatePath') { return { 'statusCode': 200, 'body': JSON.stringify({name: 'value'}) }; } else { return { 'statusCode': 406, 'body': request.body }; }", 
        "delay": {
            "timeUnit": "MICROSECONDS", 
            "value": 1
        }
    }, 
    "times": {
        "remainingTimes": 3, 
        "unlimited": false
    }
}

{
    "operationName": "transferBetweenAccounts", 
    "variables": {
        "fromAccountId": "f0174360-e3bc-4296-8237-fab71d972392", 
        "toAccountId": "83accd4c-1143-4ab2-b1f4-66da3da6c4ef", 
        "amount": "5"
    }, 
	"query":"mutation transferBetweenAccounts($fromAccountId: ID!, $toAccountId: ID!, $amount: String!, $currency: String!) {\n  makeTransfer(fromAccountId: $fromAccountId, toAccountId: $toAccountId, amount: $amount)\n}\n"
}
